mapper.select:
select 

from exam
where id = #{id,jdbcType=INTEGER}
  and exam_serial_number = #{examSerialNumber,jdbcType=INTEGER}

mapper.delete:
delete from exam
where id = #{id,jdbcType=INTEGER}
  and exam_serial_number = #{examSerialNumber,jdbcType=INTEGER}

mapper.insert:
