  start_time, creator, `state`, 
  total_points, creation_time, number_of_people
  )
values (#{examSerialNumber,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{duration,jdbcType=VARCHAR}, 
  #{startTime,jdbcType=TIMESTAMP}, #{creator,jdbcType=VARCHAR}, #{state,jdbcType=VARCHAR}, 
  #{totalPoints,jdbcType=INTEGER}, #{creationTime,jdbcType=TIMESTAMP}, #{numberOfPeople,jdbcType=INTEGER}
  )
